import React, { useState } from "react"; // Importing the necessary modules from React library
import { Link, useNavigate } from "react-router-dom"; // Importing the Link component from react-router-dom library
import "./SignUp.css"; // Importing the CSS styles for the SignUp component
import { API_URL }  from "../../config.js";

// Defining the Function component SignUp
const SignUp = () => {
    // State variables using useState hook
    const [role, setRole] = useState('');
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [password, setPassword] = useState('');
    const [showerr, setShowerr] = useState(''); // State to show error messages
    const navigate = useNavigate(); // Navigation hook from react-router

    // Function to handle form submission
    const register = async (e) => {
        e.preventDefault(); // Prevent default form submission

        if (phone.length !== 10) {
            setShowerr({ phone: 'Phone number is only 10 digits.' });
            return;
        }

        // API Call to register user
        const response = await fetch(`${API_URL}/api/auth/register`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                role: role,
                name: name,
                email: email,
                password: password,
                phone: phone,
            }),
        });
        const json = await response.json(); // Parse the response JSON

        if (json.authtoken) {
            // Store user data in session storage
            sessionStorage.setItem("auth-token", json.authtoken);
            sessionStorage.setItem("name", name);
            sessionStorage.setItem("phone", phone);
            sessionStorage.setItem("email", email);

            // Redirect user to home page
            navigate("/");
            window.location.reload(); // Refresh the page
        } else {
            if (json.errors) {
                for (const error of json.errors) {
                    setShowerr(error.msg); // Show error messages
                }
            } else {
                setShowerr(json.error);
            }
        }
    };

    return (
    <div className="container">
      {/* Main container with margin-top */}
      <div className="signup-grid">
        {/* Grid layout for sign-up form */}
        <div className="signup-text">
          {/* Title for the sign-up form */}
          <h1>Sign Up</h1>
        </div>
        <div className="signup-text1">
          Already a member?
            <span>
                <Link to='/login' style={{color: '#9570ff'}}> Login</Link>
            </span>
        </div>
        <div className="signup-form">
          <form onSubmit={register}>
            <div className="form-group">
              <label for="role">Role</label>
              <div className="select-group">
                <select 
                    name="role" 
                    id="role"
                    onChange={(e) => setR(e.target.value)}
                    aria-placeholder="Select a role">
                        <option>Select a role</option>
                        <option value="doctor">Doctor</option>
                        <option value="patient">Patient</option>
                </select>
                <div className="select-icon">
                  <i className="fa fa-chevron-down" aria-hidden="true"></i>
                </div>
              </div>
            </div>

            <div className="form-group">
                <label for="name">Name</label>
                <input
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    type="text"
                    name="name"
                    id="name"
                    required
                    className="form-control"
                    placeholder="Enter your name"
                    aria-describedby="helpId"
              />
            </div>

            <div className="form-group">
              <label for="phone">Phone</label>
              <input
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                type="tel"
                name="phone"
                id="phone"
                required
                className="form-control"
                placeholder="Enter your phone number"
                aria-describedby="helpId"
              />
            </div>

            <div className="form-group">
                <label for="email">Email</label>
                <input
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    type="email"
                    name="email"
                    id="email"
                    required
                    className="form-control"
                    placeholder="Enter your email"
                    aria-describedby="helpId"
                />
                {showerr && <div className="err" style={{ color: 'red' }}>{showerr}</div>}
            </div>

            <div className="form-group">
              {/* Form group for user's password */}
              <label for="password">Password</label>
              {/* Label for password input field */}
              <input
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                name="password"
                id="password"
                required
                className="form-control"
                placeholder="Enter your password"
                aria-describedby="helpId"
              />
              {/* Password input field */}
            </div>

            <div className="btn-group">
              {/* Button group for form submission and reset */}
              <button
                type="submit"
                className="btn btn-primary mb-2 mr-1 waves-effect waves-light"
              >
                Submit
              </button>
              {/* Submit button */}
              <button
                type="reset"
                className="btn btn-danger mb-2 waves-effect waves-light"
              >
                Reset
              </button>
              {/* Reset button */}
            </div>
          </form>
          {/* End of the form */}
        </div>
      </div>
    </div>
  );
};

export default SignUp; // Exporting the SignUp component to be used in other parts of the application
